import React, { useState } from 'react';

import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import RadioButton from './RadioButton';
import { linkTypes } from '../enums/linkTypes.enum';

function EnterLink() {
	const [radioValue, setRadioValue] = useState(linkTypes.VIDEO)

	return (
		<div>
			<FormControl>
				{`type: ${radioValue}`}
				<TextField error requiredlabel="" helperText="Can't be empty" />



				{
					[linkTypes.VIDEO, linkTypes.PLAYLIST].map((item, i) => (
						<RadioButton
							checked={Boolean(radioValue === item)}
							name={'video-type'}
							id={item}
							label={item}
							value={item}
							change={setRadioValue}
						/>
					))
				}
				<RadioButton
					checked={Boolean(radioValue === linkTypes.VIDEO)}
					name={'video-type'}
					id={linkTypes.VIDEO}
					label={linkTypes.VIDEO}
					value={linkTypes.VIDEO}
					change={setRadioValue}
				/>
				<RadioButton
					checked={Boolean(radioValue === linkTypes.PLAYLIST)}
					name={'video-type'}
					id={linkTypes.PLAYLIST}
					label={linkTypes.PLAYLIST}
					value={linkTypes.PLAYLIST}
					change={setRadioValue}
				/>

				<Button
					color="primary"
					variant="outlined">Submit
				</Button>
			</FormControl>

		</div>
	);
}

export default EnterLink;
