import React, { useState } from 'react';
import FormControl from '@material-ui/core/FormControl';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import RadioButton from './RadioButton';
import { linkTypes } from '../enums/linkTypes.enum';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import FormHelperText from '@material-ui/core/FormHelperText';

const text = {
	video: 'Enter video url',
	playlist: 'Enter playlist code'
};

function EnterLink() {
	const [radioValue, setRadioValue] = useState(linkTypes.VIDEO);
	const [inputValue, setInputValue] = useState('');
	const [form, setForm] = useState({ dirty: false });

	return (
		<div>
			<style jsx>
				{
					`
					.radio-buttons{
						display: flex;
						justify-content: center;
					}
					.radio-button-container{
						margin: 10px 10px;
					}
					`
				}
			</style>
			<FormControl error>
				<InputLabel htmlFor="my-input">Email address</InputLabel>
				<Input

					required={true}
					id="my-input"
					aria-describedby="my-helper-text"
					defaultValue={inputValue} onChange={(event) => setInputValue(event.target.value)} />

				<FormHelperText id="my-helper-text">Can't be empty</FormHelperText>
			</FormControl>

			<div className="radio-buttons">
				{
					[linkTypes.VIDEO, linkTypes.PLAYLIST].map((item, i) => (
						<div key={i} className="radio-button-container">
							<RadioButton
								checked={Boolean(radioValue === item)}
								name={'video-type'}
								id={item}
								label={item}
								value={item}
								change={setRadioValue}
							/>
						</div>
					))
				}
			</div>

			<Button
				color="primary"
				variant="outlined">
				Submit
			</Button>

		</div>
	);
}

export default EnterLink;
